const startingtemp = 20;
const startinghum = 50;
const location = "Brussels";
const sensor = "DummyDataZuza";

var spread = 0.5
var randomtemp = Math.random() * ((startingtemp + spread)
    - (startingtemp - spread) + 1) + (startingtemp - spread);
var randomhum = Math.random() * ((startinghum + spread)
    - (startinghum - spread) + 1) + (startinghum - spread);
var humidity = Math.round((randomhum + Number.EPSILON) * 10) / 10;
var temperature = Math.round((randomtemp + Number.EPSILON) * 10) / 10;

msg.payload = "{\"Sensor\":\"" + sensor + "\",\"Location\":\"" + location + "\",\"Temperature\":" + temperature + ",\"Humidity\":" + humidity + "}"

return msg;

